      1305: { breve: "Nessun record per canale e asse", descrizione: "Nessun record per canale e asse", risposta: "Nessuna risposta" },
      1400: { breve: "Errore durante il caricamento o il salvataggio del record di dati", descrizione: "Errore durante il caricamento o il salvataggio del record di dati", risposta: "Blocco impulso" },
      1401: { breve: "Nessun file di parametro valido trovato", descrizione: "Nessun file di parametro valido trovato", risposta: "Blocco impulso" },
