      1937: { breve: "I dati effettivi non sono stati raccolti in tempo", descrizione: "I dati effettivi (dati del produttore) non sono stati raccolti in tempo", risposta: "Blocco impulso" },
      1938: { breve: "Guasto del valore impostato ai dati ciclici", descrizione: "Guasto del valore impostato ai dati ciclici", risposta: "Blocco impulso" },
      1939: { breve: "La lunghezza del file è inferiore ai dati trasmessi", descrizione: "La lunghezza del file è inferiore alla lunghezza dei dati trasmessi. Sono stati trasmessi più dati di quanto dovrebbe essere la lunghezza del file, secondo il comando.", risposta: "Nessuna risposta" },
